extension.install:
if(!function_exists('imagejpeg'))
	message('This extension requires the <a href="http://www.php.net/gd">PHP GD library</a> to be installed.');

if (!isset($forum_config['o_KCAPTCHA_register']))
{
	$query = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '\'o_KCAPTCHA_register\', \'1\''
	);
	$forum_db->query_build($query) or error(__FILE__, __LINE__);
}

if (!isset($forum_config['o_KCAPTCHA_guestpost']))
{
	$query = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '\'o_KCAPTCHA_guestpost\', \'1\''
	);
	$forum_db->query_build($query) or error(__FILE__, __LINE__);
}

if (!isset($forum_config['o_KCAPTCHA_login']))
{
	$query = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '\'o_KCAPTCHA_login\', \'1\''
	);
	$forum_db->query_build($query) or error(__FILE__, __LINE__);
}

if (!isset($forum_config['o_KCAPTCHA_restorepass']))
{
	$query = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '\'o_KCAPTCHA_restorepass\', \'1\''
	);
	$forum_db->query_build($query) or error(__FILE__, __LINE__);
}

hooks.hook:
// Load the captcha language file
if (file_exists($ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php'))
	require $ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php';
else
	require $ext_info['path'].'/lang/English/'.$ext_info['id'].'.php';
	require $ext_info['path'].'/kcaptcha.php';
	session_start();

if ($forum_config['o_KCAPTCHA_login'] == '1')
{
	if (empty($errors));

	// Output CAPTCHA if first attempts failed
	if (!isset($_SESSION['KCAPTCHA_logins']))
		$_SESSION['KCAPTCHA_logins'] = 1;
	else
		$_SESSION['KCAPTCHA_logins']++;

	if ($_SESSION['KCAPTCHA_logins'] > 5)
	{
?>
				<div class="sf-set set<?php echo ++$forum_page['item_count'] ?>">
					<img src="<?php echo $ext_info['path']?>/?<?php echo session_name()?>=<?php echo session_id()?>" alt="" />
					<div class="sf-box text required">
						<label for="fld<?php echo ++$forum_page['fld_count'] ?>"><span><?php echo $lang_pun_antispam['Captcha'] ?> <em><?php echo $lang_common['Required'] ?></em></span> <small><?php echo $lang_pun_antispam['Captcha Info'] ?></small></label><br />
						<span class="fld-input"><input id="fld<?php echo $forum_page['fld_count'] ?>" type="text" name="keystring" value="" size="20" maxlength="10" /></span>
					</div>
				</div>
<?php
	}
}

if ($forum_user['is_guest'] && $forum_config['o_KCAPTCHA_guestpost'] == '1')
{
	if (empty($_SESSION['KCAPTCHA_confirmed_user']))
	{
		if(isset($_SESSION['captcha_keystring']) && $_SESSION['captcha_keystring'] != $_POST['keystring']){
			if (!isset($_POST['preview']))
				$errors[] = $lang_pun_antispam['Invalid Text'];
		}
		else
			$_SESSION['KCAPTCHA_confirmed_user'] = 1;
	}

	// Post is to be written to DB, ask CAPTCHA for the next posting
	if (empty($errors) && !isset($_POST['preview']))
		$_SESSION['KCAPTCHA_confirmed_user'] = 0;
}
